const MarketplaceScreen = ({ navigation }) => {
  const [searchQuery, setSearchQuery] = useState("")
  const [selectedListing, setSelectedListing] = useState(null)
  const [activeTab, setActiveTab] = useState("buy") // 'buy' or 'sell'

  const filteredListings = listingsData.filter(
    (listing) =>
      listing.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      listing.location.toLowerCase().includes(searchQuery.toLowerCase()),
  )

  const renderListingItem = ({ item }) => (
    <TouchableOpacity style={styles.listingCard} onPress={() => setSelectedListing(item)} activeOpacity={0.7}>
      <Image source={{ uri: item.imageUrl }} style={styles.listingImage} resizeMode="cover" />
      <View style={styles.listingCardContent}>
        <Text style={styles.listingTitle}>{item.title}</Text>
        <Text style={styles.listingPrice}>
          Rs. {item.price} / {item.quantity}
        </Text>

        <View style={styles.listingCardFooter}>
          <View style={styles.locationContainer}>
            <Ionicons name="location" size={14} color="#666" />
            <Text style={styles.locationText}>{item.location}</Text>
          </View>
          <Text style={styles.postedDate}>{item.postedDate}</Text>
        </View>
      </View>
    </TouchableOpacity>
  )

  return (
    <SafeAreaView style={styles.safeArea}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="#ff9800" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Marketplace</Text>
        <TouchableOpacity style={styles.filterButton}>
          <Ionicons name="filter" size={22} color="#ff9800" />
        </TouchableOpacity>
      </View>

      <View style={styles.tabContainer}>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === "buy" ? styles.tabButtonActive : null]}
          onPress={() => setActiveTab("buy")}
        >
          <Ionicons name="cart" size={18} color={activeTab === "buy" ? "#ff9800" : "#666"} style={styles.tabIcon} />
          <Text style={[styles.tabButtonText, activeTab === "buy" ? styles.tabButtonTextActive : null]}>Buy Crops</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.tabButton, activeTab === "sell" ? styles.tabButtonActive : null]}
          onPress={() => {
            setActiveTab("sell")
            Alert.alert(
              "Sell Crops",
              "This feature would allow farmers to list their crops for sale. Would you like to create a listing?",
              [
                {
                  text: "Cancel",
                  style: "cancel",
                },
                {
                  text: "Create Listing",
                  onPress: () =>
                    Alert.alert(
                      "Feature Coming Soon",
                      "The ability to create listings will be available in the next update.",
                    ),
                },
              ],
            )
          }}
        >
          <Ionicons
            name="pricetag"
            size={18}
            color={activeTab === "sell" ? "#ff9800" : "#666"}
            style={styles.tabIcon}
          />
          <Text style={[styles.tabButtonText, activeTab === "sell" ? styles.tabButtonTextActive : null]}>
            Sell Crops
          </Text>
        </TouchableOpacity>
      </View>

      {selectedListing ? (
        <ScrollView style={styles.detailsContainer} showsVerticalScrollIndicator={false}>
          <TouchableOpacity style={styles.backToListButton} onPress={() => setSelectedListing(null)}>
            <Ionicons name="chevron-back" size={16} color="#ff9800" />
            <Text style={styles.backToListText}>Back to listings</Text>
          </TouchableOpacity>

          <Image source={{ uri: selectedListing.imageUrl }} style={styles.detailImage} resizeMode="cover" />

          <View style={styles.detailContent}>
            <Text style={styles.detailTitle}>{selectedListing.title}</Text>
            <Text style={styles.detailPrice}>
              Rs. {selectedListing.price} / {selectedListing.quantity}
            </Text>

            <View style={styles.sellerSection}>
              <View style={styles.sellerHeader}>
                <Text style={styles.sectionTitle}>Seller Information</Text>
                {selectedListing.seller.verified && (
                  <View style={styles.verifiedBadge}>
                    <Ionicons name="checkmark-circle" size={14} color="white" />
                    <Text style={styles.verifiedText}>Verified</Text>
                  </View>
                )}
              </View>

              <View style={styles.sellerInfo}>
                <Text style={styles.sellerName}>{selectedListing.seller.name}</Text>
                <View style={styles.ratingContainer}>
                  <Ionicons name="star" size={16} color="#FFD700" />
                  <Text style={styles.ratingText}>{selectedListing.seller.rating}</Text>
                </View>
              </View>

              <View style={styles.locationDetailContainer}>
                <Ionicons name="location" size={16} color="#666" />
                <Text style={styles.locationDetailText}>{selectedListing.location}</Text>
              </View>
            </View>

            <View style={styles.descriptionSection}>
              <Text style={styles.sectionTitle}>Description</Text>
              <Text style={styles.descriptionText}>{selectedListing.description}</Text>
            </View>

            <View style={styles.postedSection}>
              <Text style={styles.postedText}>Posted: {selectedListing.postedDate}</Text>
            </View>

            <View style={styles.actionButtons}>
              <TouchableOpacity
                style={styles.contactButton}
                onPress={() => Alert.alert("Contact Seller", "This would open a chat with the seller in a real app.")}
              >
                <Ionicons name="chatbubble" size={18} color="white" />
                <Text style={styles.contactButtonText}>Contact Seller</Text>
              </TouchableOpacity>

              <TouchableOpacity
                style={styles.buyButton}
                onPress={() => Alert.alert("Purchase", "This would initiate the purchase process in a real app.")}
              >
                <Ionicons name="cart" size={18} color="white" />
                <Text style={styles.buyButtonText}>Buy Now</Text>
              </TouchableOpacity>
            </View>
          </View>
        </ScrollView>
      ) : (
        <View style={styles.listingsContainer}>
          <View style={styles.searchContainer}>
            <View style={styles.searchInputContainer}>
              <Ionicons name="search" size={20} color="#666" style={styles.searchIcon} />
              <TextInput
                style={styles.searchInput}
                placeholder="Search crops or location..."
                value={searchQuery}
                onChangeText={setSearchQuery}
                placeholderTextColor="#aaa"
              />
              {searchQuery.length > 0 && (
                <TouchableOpacity style={styles.clearButton} onPress={() => setSearchQuery("")}>
                  <Ionicons name="close-circle" size={18} color="#666" />
                </TouchableOpacity>
              )}
            </View>
          </View>

          <FlatList
            data={filteredListings}
            renderItem={renderListingItem}
            keyExtractor={(item) => item.id.toString()}
            contentContainerStyle={styles.listingsList}
            showsVerticalScrollIndicator={false}
            ListEmptyComponent={
              <View style={styles.emptyContainer}>
                <MaterialCommunityIcons name="store-off" size={50} color="#ccc" />
                <Text style={styles.emptyText}>No listings found</Text>
              </View>
            }
          />
        </View>
      )}
    </SafeAreaView>
  )
}

